(()=>{"use strict";(t=>{const e=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds");(()=>{let t=setInterval((()=>{let s=(()=>{let t=(new Date("5 august 2022").getTime()-(new Date).getTime())/1e3;return{timeRemaining:t,hours:Math.floor(t/60/60),minutes:Math.floor(t/60%60),seconds:Math.floor(t%60)}})();s.timeRemaining>0?(s.hours<10?e.textContent="0"+s.hours:e.textContent=s.hours,s.minutes<10?n.textContent="0"+s.minutes:n.textContent=s.minutes,s.seconds<10?o.textContent="0"+s.seconds:o.textContent=s.seconds):clearInterval(t)}),1e3)})()})()})();